package Operators;

public class IncrDecr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=7;
		int b=3;
		
		System.out.println(a + b);
		//				   7 + 3=10
		
		System.out.println(++a + --b + 2);
		//					8     2  + 2=12
		
		System.out.println(a++ + a);
		//					8  + 9=17
		
		System.out.println(a-- + b);
		//					9 +  2=11
	
	
		

	}

}
